import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Accordion, Card, Button } from 'react-bootstrap';

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios.get('http://localhost:5000/api/dashboard', { headers: { Authorization: token } })
        .then(response => setDashboardData(response.data.data))
        .catch(error => console.error('Error fetching dashboard data:', error.response.data.error));
    }
  }, []);

  return (
    <div className="container mt-5">
      <h2 class="lead text-center display-6">Welcome back, <span className='text-primary'>{dashboardData ? `${dashboardData.username}` : 'Loading...'}</span></h2>
      <Accordion>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">
              Container 1
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body>
              {/* Include your WeatherAPI component here */}
            </Card.Body>
          </Accordion.Collapse>
        </Card>

        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="1">
              Container 2
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="1">
            <Card.Body>
              {/* Include your WeatherAPI component here */}
            </Card.Body>
          </Accordion.Collapse>
        </Card>

        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="2">
              Container 3
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="2">
            <Card.Body>
              {/* Include your WeatherAPI component here */}
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    </div>
  );
};

export default Dashboard;
