import React, { useState, useEffect } from 'react';
import { Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const EditProfile = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
  
    // Fetch existing user data when the component mounts
    useEffect(() => {
      const fetchData = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await fetch('http://localhost:3000/api/edit-profile', {
            method: 'GET',
            headers: {
              Authorization: token,
            },
          });
  
          if (response.ok) {
            const data = await response.json();
            setUsername(data.username);
          } else {
            const data = await response.json();
            setError(data.error);
            console.error('Error fetching user data:', data.error);
          }
        } catch (error) {
          setError('Error fetching user data');
          console.error('Error fetching user data:', error.message);
        }
      };
  
      fetchData();
    }, []);
  
    const handleUpdate = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/update', {
          method: 'PUT', // Use PUT method for updates
          headers: {
            'Content-Type': 'application/json',
            Authorization: token,
          },
          body: JSON.stringify({ username, password }),
        });
  
        if (response.ok) {
          navigate('/dashboard');
        } else {
          const data = await response.json();
          setError(data.error);
          console.error('Update failed:', data.error);
        }
      } catch (error) {
        setError('Error during account update');
        console.error('Error during account update:', error.message);
      }
    };

  return (
    <div className="container mt-5">
        <div className='offset-md-3 col-md-6 col-sm-12'>
        <h1 class="text-center text-primary fw-bold">Update your profile</h1>
        <Form>
        <Form.Group controlId="formBasicUsername">
          <Form.Control className='my-2' type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Control className='my-2' type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        </Form.Group>

        <div className='d-flex justify-content-center'>
                <Button className='btn btn-primary btn-md' onClick={handleUpdate}> Update
                </Button>
            </div>
            <hr/>
        {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
        </Form>
        </div>
    </div>
  );
};

export default EditProfile;
