import React, { useEffect, useState } from 'react';

const Dashboard = () => {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Retrieve the token from localStorage
    const token = localStorage.getItem('token');

    // If the token exists, the user is logged in
    if (token) {
      // Perform any additional authentication checks if needed

      // Get the username from the token or your authentication mechanism
      const decodedToken = decodeToken(token);
      const user = decodedToken.username;
      setUsername(user);
    } else {
      // Redirect the user to the login page if not authenticated
      // You can use useHistory or useNavigate for this
    }
  }, []);

  // Function to decode the JWT token
  const decodeToken = (token) => {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
    return JSON.parse(jsonPayload);
  };

  return (
    <div className="container mt-5">
      <h1>Welcome, {username}!</h1>
      {/* Other dashboard content goes here */}
    </div>
  );
};

export default Dashboard;
