import React, { useState, useEffect } from 'react';
import { Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditProfile = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null); // New state for error

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user-profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        const userData = response.data;
        setUsername(userData.username);
      } catch (error) {
        console.error('Error fetching user data:', error.response.data.error);
      }
    };

    fetchUser();
  }, []);

  const handleUpdate = async () => {
    try {
      const response = await axios.put('http://localhost:5000/api/update-profile', {
        username,
        password,
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      if (response.ok) {
        const data = response.data;
        const token = data.token;

        if (token) {
          localStorage.setItem('token', token);
          navigate('/dashboard');
        } else {
          console.error('Token not received in update response');
        }
      } else {
        const data = response.data;
        setError(data.error); // Set error state
        console.error('Update failed:', data.error);
      }
    } catch (error) {
      setError('Error during account update'); // Set error state
      console.error('Error during account update:', error.message);
    }
  };

  return (
    <div className="container mt-5">
        <div className='offset-md-3 col-md-6 col-sm-12'>
        <h1 class="text-center text-primary fw-bold">Update your profile</h1>
        <Form>
        <Form.Group controlId="formBasicUsername">
          <Form.Control className='my-2' type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Control className='my-2' type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        </Form.Group>

        <div className='d-flex justify-content-center'>
                <Button className='btn btn-primary btn-md' onClick={handleUpdate}> Update
                </Button>
            </div>
            <hr/>
        {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
        </Form>
        </div>
    </div>
  );
};

export default EditProfile;
